  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.2);
  margin-bottom: 2rem;
}

.container::before {
  display: block;
  background-color: transparent;
  content: "";
  top: 5px;
  left: 0.5%;
  width: 99%;
  height: 100%;
  z-index: -200;
